import ProjectCard from "./ProjectCard";
import { v4 as uuid } from "uuid";
import { state, useState }  from "react";

export default function ProjectList ({ user }) {
    const [ title, setTitle ] = useState("");
    const [ description, setDescription ] = useState("");
    const [ timeframe, setTimeframe ] = useState("");
    const [ route, setRoute ] = useState("");
    const [ languages, setLanguages ] = useState("");
    const [ awards, setAwards ] = useState("");

    const projects = user.projects.map((p) => {
        <ProjectCard key={uuid()} project={p} />
    })

    function handleAddProject() {

    }

    return (
        <>
        <h1>Your Projects</h1>
        {projects}

        <h1>Add Projects</h1>
        <form onSubmit={handleAddProject}>
            <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Project Title"
            />
            <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Project Description"
            />
            <input
            type="text"
            value={timeframe}
            onChange={(e) => setTimeframe(e.target.value)}
            placeholder="Project Timeframe"
            />
            <input
            type="text"
            value={route}
            onChange={(e) => setRoute(e.target.value)}
            placeholder="Project Route"
            />
            <input
            type="text"
            value={languages}
            onChange={(e) => setLanguages(e.target.value)}
            placeholder="Project Languages"
            />
            <input
            type="text"
            value={awards}
            onChange={(e) => setAwards(e.target.value)}
            placeholder="Project Awards"
            />
            <button type="submit">Add Project</button>
        </form>

        <h1>Edit Your Profile</h1>
        <form onSubmit={handleEdit}>
          <input
            type="text"
            id="username"
            autoComplete="off"
            value={username}
            placeholder="Username"
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="text"
            id="email"
            autoComplete="off"
            value={email}
            placeholder="E-mail Address"
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text"
            id="imageUrl"
            value={imageUrl}
            placeholder="Profile Image URL"
            onChange={(e) => setImageUrl(e.target.value)}
          />
          <input
            rows="3"
            id="bio"
            value={bio}
            placeholder="Enter your Bio"
            onChange={(e) => setBio(e.target.value)}
          />
          <input
            type="password"
            id="password"
            value={password}
            placeholder="Password"
            onChange={(e) => setPassword(e.target.value)}
            autoComplete="current-password"
          />
          <input
            type="password"
            id="password_confirmation"
            value={passwordConfirmation}
            placeholder="Confirm Password"
            onChange={(e) => setPasswordConfirmation(e.target.value)}
            autoComplete="current-password"
          />
          <button type="submit">{isLoading ? "Loading..." : "Sign Up"}</button>
          {errors.map((err) => (
            <p key={err}>{err}</p>
          ))}
        </form>
        </>
    )
}
