import ProjectCard from "./ProjectCard";
import { v4 as uuid } from "uuid";
import { state, useState }  from "react";

export default function ProjectList ({ user, setUser }) {
    const [ title, setTitle ] = useState("");
    const [ description, setDescription ] = useState("");
    const [ timeframe, setTimeframe ] = useState("");
    const [ route, setRoute ] = useState("");
    const [ languages, setLanguages ] = useState("");
    const [ awards, setAwards ] = useState("");
    const [formErrors, setFormErrors] = useState([]);

    const projects = user.projects.map((p) => {
        return (
        <ProjectCard key={uuid()} project={p} />
        )
    })

    function handleAddProject(e) {
        e.preventDefault();
        const newProject = {
            title: title,
            description: description,
            timeframe: timeframe,
            route: route,
            languages: languages,
            awards: awards,
        };
        fetch("/projects", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newProject),
        }).then((resp) => {
            if (resp.ok) {
                resp.json().then((obj) => {
                    // pushIntoState(obj);
                    setFormErrors([]);
                });
            } else {
                resp.json().then((err) => setFormErrors(err.errors));
            }
        });
    }

    return (
        <>
        <h1>Your Projects</h1>
        {projects}

        <h1>Add Projects</h1>
        <form onSubmit={handleAddProject}>
            <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Project Title"
            />
            <br />
            <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Project Description"
            />
            <br />
            <input
            type="text"
            value={timeframe}
            onChange={(e) => setTimeframe(e.target.value)}
            placeholder="Project Timeframe"
            />
            <br />
            <input
            type="text"
            value={route}
            onChange={(e) => setRoute(e.target.value)}
            placeholder="Project Route"
            />
            <br />
            <p>Seperate Awards & Languages using ", "</p>
            <input
            type="text"
            value={languages}
            onChange={(e) => setLanguages(e.target.value)}
            placeholder="Project Languages"
            />
            <br />
            <input
            type="text"
            value={awards}
            onChange={(e) => setAwards(e.target.value)}
            placeholder="Project Awards"
            />
            <br />
            <br />
            <button type="submit">Add Project</button>
            {formErrors.length > 0
            ? formErrors.map((err) => (
            <p key={err} style={{ color: "red" }}>
              {err}
            </p>
          ))
        : null}
        </form>
        </>
    )
}
